import "./Video.scss";
import Likes from "../../assets/Images/likes.svg"
import Views from "../../assets/Images/views.svg"

function Video(props) {
  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const formattedDate = `${date.getMonth() + 1}/${date.getDate() + 1}/${date.getFullYear()}`;
    return formattedDate;
  };
    return(
      <>
      <div className="videocontainer">
        <video
      className="videocontainer__video"
      poster={props.selectedVideo.image}
      controls
    ></video>
        </div>

      <div className="videoName">
        <h1>{props.selectedVideo.title}</h1>
      </div>

      <div className="data">

        <div className="data__item">
        <p className="data__itemchannel">By {props.selectedVideo.channel}</p>
        <p className="data__date">{formatDate(props.selectedVideo.timestamp)}</p>
        </div>

        <div className="data__item">
        <p className="data__icons"> <img className="dataitem__item--views" src={Views} alt="Views"></img> {props.selectedVideo.views}</p>
        <p className="dataitem__icons"> <img className="dataitem__item--likes" src={Likes} alt="Likes"></img> {props.selectedVideo.likes}</p>
        </div>

      </div>

      <div className="description">
      <p>{props.selectedVideo.description}</p>
      </div>


      </>
    )
}

export default Video
